import styled from 'styled-components';
import { themeGet } from '@styled-system/theme-get';

const CheckoutWrapper = styled.div`
  background-color: ${themeGet('colors.lightColor', '#F7F7F7')};
  position: relative;
  padding: 130px 0 60px 0;

  @media (max-width: 990px) {
    padding: 0;
    padding-top: 60px;
  }
`;

export const CheckoutContainer = styled.div`
  background-color: #fff;
  border: 1px solid ${themeGet('colors.borderColor', '#f1f1f1')};
  padding: 60px;
  border-radius: 6px;
  overflow: hidden;
  position: relative;
  @media (min-width: 990px) {
    width: 870px;
    margin-left: auto;
    margin-right: auto;
  }

  @media (max-width: 480px) {
    padding: 30px;
  }
`;

export const Heading = styled.h3`
  font-family: 'Poppins', sans-serif;
  font-size: ${themeGet('fontSizes.2', '15')}px;
  font-weight: ${themeGet('fontWeights.6', '700')};
  color: ${themeGet('colors.darkBold', '#0D1136')};
  line-height: 1.2;
  margin-bottom: 25px;
`;

export const CheckoutHead = styled.div`
  display: flex;
  align-items: center;
  position: relative;
  &:before {
    content: '';
    position: absolute;
    width: 1px;
    height: 100%;
    display: block;
    background-color: ${themeGet('colors.borderColor', '#f1f1f1')};
    top: 0;
    left: 50%;
  }

  span {
    font-family: 'Lato', sans-serif;
    font-size: ${themeGet('fontSizes.2', '15')}px;
    font-weight: ${themeGet('fontWeights.3', '400')};
    color: ${themeGet('colors.darkBold', '#0D1136')};
    margin-bottom: 15px;
  }

  h3 {
    display: block;
    font-family: 'Poppins', sans-serif;
    font-size: ${themeGet('fontSizes.2', '15')}px;
    font-weight: ${themeGet('fontWeights.6', '700')};
    color: ${themeGet('colors.darkBold', '#0D1136')};
  }
`;

export const TotalProduct = styled.div`
  flex-grow: 1;
  text-align: right;
  padding-right: 60px;
`;

export const TotalPrice = styled.div`
  flex-grow: 1;
  text-align: left;
  padding-left: 60px;
`;

export const DeliverySchedule = styled.div`
  margin-top: 60px;

  .radioGroup {
    justify-content: space-between;
    > label {
      margin-right: 0;
      flex: calc(33.3333333333% - 10px);
      max-width: calc(33.3333333333% - 10px);
      padding: 11px 15px;

      @media (max-width: 700px) {
        flex: calc(50% - 10px);
        max-width: calc(50% - 10px);
      }

      @media (max-width: 480px) {
        flex: 100%;
        max-width: 100%;
        margin-right: 0;
      }
    }
  }
`;

export const DeliveryAddress = styled.div`
  margin-top: 30px;
`;

export const ButtonGroup = styled.div`
  display: flex;
  flex-direction: column;

  .radioGroup {
    flex-grow: 1;
    justify-content: space-between;

    label {
      margin-top: 0;
      flex: calc(33.333333333% - 10px);
      max-width: calc(33.333333333% - 10px);
      margin-bottom: 15px;

      &:nth-child(3n) {
        margin-right: 0;
      }

      @media (max-width: 700px) {
        flex: calc(50% - 10px);
        max-width: calc(50% - 10px);

        &:nth-child(3n) {
          margin-right: 15px;
        }

        &:nth-child(2n) {
          margin-right: 0;
        }
      }

      @media (max-width: 480px) {
        flex: 100%;
        max-width: 100%;
        margin-right: 0;

        &:nth-child(3n) {
          margin-right: 0;
        }

        &:nth-child(2n) {
          margin-right: 0;
        }
      }
    }
  }

  .reusecore__button {
    flex: calc(33.3333333333% - 10px);
    max-width: calc(33.3333333333% - 10px);
    flex-shrink: 0;
    height: auto;
    min-height: 77px;
    border: 1px dashed ${themeGet('colors.borderColor', '#f1f1f1')};
    margin-bottom: 15px;
    margin-left: 0;
    margin-right: auto;
    &:hover {
      border-color: ${themeGet('colors.primary', '#00b5f4')};
    }

    @media (max-width: 700px) {
      flex: calc(50% - 10px);
      max-width: calc(50% - 10px);
    }

    @media (max-width: 480px) {
      flex: 100%;
      max-width: 100%;
    }
  }
`;

export const Contact = styled.div`
  margin-top: 30px;
`;

export const PaymentOption = styled.div`
  margin-top: 60px;
  padding-top: 60px;
  position: relative;
  &:before {
    content: '';
    position: absolute;
    width: 150%;
    height: 1px;
    top: 0;
    left: 50%;
    transform: translateX(-50%);
    display: block;
    background: ${themeGet('colors.borderColor', '#f1f1f1')};
  }
`;

export const PaymentCardList = styled.div`
  display: flex;
  align-items: stretch;
  justify-content: space-between;
  margin-top: 15px;

  .payment-card-radio {
    display: block;
    cursor: pointer;
    position: relative;
    > input {
      position: absolute;
      top: 0;
      left: 0;
      opacity: 0;
      &:checked {
        & ~ .payment-card {
          border-color: ${themeGet('colors.primary', '#00b5f4')};
        }
      }
    }
    .payment-card {
      pointer-events: none;
    }
  }
`;

export const SavedCard = styled.div`
  flex: calc(100% - 120px);
  max-width: calc(100% - 120px);

  .saved-card-title {
    font-family: 'Lato', sans-serif;
    font-size: ${themeGet('fontSizes.1', '13')}px;
    font-weight: ${themeGet('fontWeights.6', '700')};
    color: ${themeGet('colors.primary', '#00b5f4')};
    margin-bottom: 10px;
    display: block;
  }
`;

export const AddCard = styled.div`
  flex: 105px;
  max-width: 105px;
  flex-shrink: 0;
  display: flex;
  flex-direction: column;
  .reusecore__button {
    flex-grow: 1;
    height: auto;
    border: 1px dashed ${themeGet('colors.borderColor', '#f1f1f1')};
    border-radius: 6px;
    padding: 0 5px;
    flex-direction: column;
    svg {
      width: 18px;
      height: auto;
    }
    .btn-icon {
      margin-bottom: 5px;
    }
    &:hover {
      border-color: ${themeGet('colors.primary', '#00b5f4')};
    }
  }
  .safe-label {
    font-family: 'Lato', sans-serif;
    font-size: ${themeGet('fontSizes.1', '13')}px;
    font-weight: ${themeGet('fontWeights.6', '700')};
    color: ${themeGet('colorsdarkRegular', '#77798C')};
    display: flex;
    align-items: center;
    justify-content: flex-end;
    margin-bottom: 10px;
    > svg {
      fill: currentColor;
      margin-right: 4px;
    }
  }
`;

export const OtherPayOption = styled.div`
  margin-top: 15px;
  display: flex;
  align-items: stretch;

  .other-pay-radio {
    flex-grow: 1;
    span {
      display: block;
      border-radius: 6px;
      background-color: ${themeGet('colors.lightColor', '#F7F7F7')};
      border: 1px solid ${themeGet('colors.lightColor', '#F7F7F7')};
      text-align: center;
      padding: 12px 20px;
      cursor: pointer;
      color: ${themeGet('colors.darkBold', '#0D1136')};
      font-family: 'Lato', sans-serif;
      font-size: ${themeGet('fontSizes.1', '13')}px;
      font-weight: ${themeGet('fontWeights.3', '400')};
      line-height: 24px;
      -webkit-transition: all 0.25s ease;
      transition: all 0.25s ease;
    }
    input {
      position: absolute;
      top: 0;
      left: 0;
      opacity: 0;
      &:checked {
        & ~ span {
          border-color: ${themeGet('colors.primary', '#00b5f4')};
          background-color: #ffffff;
        }
      }
    }
    &.cash-on-delivery {
      flex: 320px;
      max-width: 230px;
      margin-left: 15px;
    }
  }
`;

export const CheckoutSubmit = styled.div`
  margin-top: 60px;
  .reusecore__button {
    width: 100%;
  }
`;

export const PaymentGroupWrapper = styled.div``;

export const AddAddressWrapper = styled.div`
  textarea {
    font-family: lato;
  }
  .field-wrapper {
    margin-bottom: 30px;
  }
`;

export const AddcontactWrapper = styled.div`
  textarea {
    font-family: lato;
  }
  .field-wrapper,
  .button_outline {
    margin-bottom: 30px;
  }
  .button_outline {
    border: 1px dashed ${themeGet('colors.borderColor', '#f1f1f1')};
    .btn-icon {
      margin-right: 5px;
    }
    &:hover {
      border-color: ${themeGet('colors.primary', '#00b5f4')};
    }
  }
`;

export const AuthenticationOverlay = styled.div`
  width: 100%;
  height: 100%;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;
  background-color: rgba(255, 255, 255, 0.9);
  position: absolute;
  top: 0;
  left: 0;
  bottom: 0;
  right: 0;
  padding-top: 300px;
  z-index: 100;

  button {
    box-shadow: 0 0 36px rgba(0, 0, 0, 0.16);
  }
`;

export const InfoText = styled.span`
  font-family: 'Lato', sans-serif;
  font-size: ${themeGet('fontSizes.2', '15')}px;
  font-weight: ${themeGet('fontWeights.3', '400')};
  color: ${themeGet('colors.darkBold', '#0D1136')};
  margin-bottom: 15px;
  margin-top: 15px;
`;

export const OrderSummary = styled.div`
  width: 100%;
  display: flex;
  flex-direction: column;
  padding: 60px;
  border-radius: 6px;
  background-color: ${themeGet('colors.lightColor', '#F7F7F7')};

  @media (max-width: 767px) {
    padding: 30px;
  }
`;

export const OrderLabel = styled.span`
  color: ${themeGet('colors.darkBold', '#0D1136')};
  font-family: 'Lato', sans-serif;
  font-size: ${themeGet('fontSizes.2', '15')}px;
  font-weight: ${themeGet('fontWeights.3', '400')};
  line-height: 24px;
`;

export const OrderAmount = styled.span`
  font-family: 'Lato', sans-serif;
  font-size: ${themeGet('fontSizes.2', '15')}px;
  font-weight: ${themeGet('fontWeights.3', '400')};
  color: ${themeGet('colors.darkBold', '#0D1136')};
  line-height: 24px;
  font-weight: 700;
`;

export const OrderSummaryItem = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  position: relative;

  &.voucherWrapper {
    @media (max-width: 630px) {
      flex-direction: column;
      align-items: flex-start;

      ${OrderLabel} {
        margin-bottom: 10px;
      }
    }
  }

